//Funcionalidades para el menu en version movil
var abrirMenu = true;
function toggleOpciones(){
    if(abrirMenu){
        $(".container-menu-movil").animate({
            left: '0'
        })
        $(".page-content").css("opacity","0.4")
        $("body").css("overflow","hidden");
        abrirMenu = false;
    }else{
        $(".container-menu-movil").animate({
            left: '-100%'
        })
        $(".page-content").css("opacity","");
        $("body").css("overflow","");
        abrirMenu = true;
    }
}

function deleteCookies(){
    document.cookie = "Modulo=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
    document.cookie = "Gestion=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
}
function setCookie(cname, cvalue) {        
    document.cookie = cname + "=" + cvalue + ";path=/";
}

$(function(){
    
    //funcionalidades para el menu en version movil
    $("#btnMenuMovil").click(function(){
        window.scrollTo(0,0);
        toggleOpciones();        
    })

    $(".page-content").click(function(e){
	    if(abrirMenu == false){
            toggleOpciones();
        }        
    })

    $("#container-menu-desktop .gestiones a").click(function(){
        var texto = $(this).text();
        setCookie("Gestion", $.trim(texto));
        
        var headerMenu = $(this).parent().parent().parent();        
        var textHeaderMenu = headerMenu.find("a.title-modulo").text();
        setCookie("Modulo", $.trim(textHeaderMenu))        
        
    })

    
})
